let secret = env.get('JWTSECRET');
let options = { expiresIn: '2 days' };
let token = jwt.sign(msg.sub, secret, options);
let decoded = jwt.verify(token, secret);
msg.sub = {
    token: token,
    decoded: decoded
}
return msg;

let secret = env.get('JWTSECRET');
let error = null;
let decoded = null;
jwt.verify(msg.sub, secret, (err, dec) => {
    if(err) error = err;
    decoded = dec;
});
msg.sub = {
    token: msg.sub,
    decoded: decoded,
    error: error
}
return msg;

let pass = bcrypt.hashSync(msg.sub, 10);
msg.sub = pass;
return msg;

let {password, hash} = msg.sub;
let rs = '';
rs = bcrypt.compareSync(password, hash);
msg.sub = {
    isValid: rs
};
return msg;

let algorithm = 'aes256';
let key = env.get('CRYPTO_KEY');
let iv = env.get('CRYPTO_IV');
let cipher = crypto.createCipheriv(algorithm, key, iv);
let encrypted = cipher.update(msg.sub, 'utf8', 'hex') + cipher.final('hex');
msg.sub = encrypted;
return msg;

let algorithm = 'aes256';
let key = env.get('CRYPTO_KEY');
let iv = env.get('CRYPTO_IV');
let decipher = crypto.createDecipheriv(algorithm, key, iv);
let decrypted = decipher.update(msg.sub, 'hex', 'utf8') + decipher.final('utf8');
msg.sub = decrypted;
return msg;

let id = msg.req.params.id;
msg.found = msg.payload.data.some(x => x.code === id);
if(msg.found){
    msg.result = msg.payload.data.find(x => x.code === id);
    msg.payload = {
        key: ["schema", "page"]
    }
}else{
    msg.payload = "not found";
}
return msg;

msg.result['pages'] = msg.payload.map(x => x.value);
msg.payload = {
    key : msg.req.params.id
};
return msg;

let id = msg.req.params.id;
msg.result['navigation'] = msg.payload.find(x => x.value.applicationID === id)?.value;
msg.payload = msg.result;
return msg;

msg.corpRoles = msg.payload.data;
let users = msg.users.map(x => {
    let algorithm = 'aes256';
    let key = env.get('CRYPTO_KEY');
    let iv = env.get('CRYPTO_IV');
    let cipher = crypto.createCipheriv(algorithm, key, iv);
    let encrypted = cipher.update(`${x.NIK};pass`, 'utf8', 'hex') + cipher.final('hex');
    let type = 'non-adfs';
    if (/^\d+$/.test(x.NIK) && x.NIK.length === 8) type = 'adfs';

    return {
        nik: x.NIK,
        type: type,
        fullName: x.szFullName,
        email: x.szEmailAddress,
        occupation: msg.corpRoles.find(r => r.origin === x.szCorpRoleName)?.code || null,
        roles: msg.sysRoles.filter(r => r.origin === x.szSysRoleName).map(r => r.code),
        authorizations: [
            {
                type: 'password',
                passwords: [encrypted]
            }
        ]
    }
});
msg.documents = {
    docs: users
};
return msg;

let root = msg.payload.filter(x => x.ParentID === null);
let rs = [];
root.forEach(x => {
    rs.push({
        id: x.iOrgID,
        name: x.ChildName.toUpperCase(),
        desc: x.ChildDesc,
        category: x.cCategory.toUpperCase(),
        children: getHierarchy(msg.payload, x.iOrgID)
    });
});
msg.payload = rs;
return msg;

function getHierarchy(data, parentID, limit = 0) {
    if (limit > 10) {
        return;
    }
    let result = [];
    for (let i = 0; i < data.length; i++) {
        let item = data[i];
        if (item.ParentID === parentID) {
            result.push({
                id: item.iOrgID,
                name: item.ChildName.toUpperCase(),
                desc: item.ChildDesc,
                category: item.cCategory.toUpperCase(),
                children: (data.some(x => x.ParentID === parentID)) ? getHierarchy(data, item.iOrgID, limit + 1) : null
            })
        }
    }
    return result;
}

