function findNestedObj(entireObj, keyToFind, valToFind) {
    let foundObj;
    JSON.stringify(entireObj, (_, nestedValue) => {
        if (nestedValue && nestedValue[keyToFind] === valToFind) {
            foundObj = nestedValue;
        }
        return nestedValue;
    });
    return foundObj;
}

function flatten(arr, level = 0, filter = {}, parentId = null) {
    const filterKeys = Object.keys(filter);
    return arr.reduce((acc, { id, children, ...rest }) => {
        const flattenedChildren = children ? flatten(children, level + 1, filter, id) : [];
        const flattenedObject = { ...rest, id, level, parentId };
        if (filterKeys.every(key => flattenedObject[key] === filter[key])) {
            return [...acc, flattenedObject, ...flattenedChildren];
        }
        return acc.concat(flattenedChildren);
    }, []);
}

function filterFlat(flatObj, crit) {
    return flatObj.filter(x => {
        let params = [];
        crit.forEach(c => {
            let rs = false;
            if (Array.isArray(c.val)) {
                rs = c.val.some(s => x[c.key] === s)
            } else {
                rs = x[c.key] === c.val
            }
            params.push(rs);
        });
        return params.reduce((acc, val) => acc && val, true);
    });
}

function getParents(flatObj, childId) {
    let parents = [];
    let child = flatObj.find(x => x.id === childId);
    if(child){
        parents.push(child);
        do {
            let parent = (child) ? flatObj.find(x => x.id === child.parentId) : [];
            parents.push(parent);
            child = parent;
        }
        while (child && child.parentId != null);
    }
    return parents;
}

