function flatten(arr, level = 0, filter = {}, parentId = null) {
    const filterKeys = Object.keys(filter);
    return arr.reduce((acc, { id, children, ...rest }) => {
        const flattenedChildren = children ? flatten(children, level + 1, filter, id) : [];
        const flattenedObject = { ...rest, id, level, parentId };
        if (filterKeys.every(key => flattenedObject[key] === filter[key])) {
            return [...acc, flattenedObject, ...flattenedChildren];
