        return acc.concat(flattenedChildren);
    }, []);
}

function getParents(flatObj, childId) {
    let parents = [];
    let child = flatObj.find(x => x.id === childId);
    if (child) {
        parents.push(child);
        do {
            let parent = (child) ? flatObj.find(x => x.id === child.parentId) : [];
            if (parent) parents.push(parent);
            child = parent;
        }
        while (child && child.parentId != null);
    }
    return parents;
}

function combineArrays(...arrays) {
    const combinedArray = [].concat(...arrays);
    const uniqueObject = {};
    const uniqueArray = [];

    for (let obj of combinedArray) {
        const objString = JSON.stringify(obj);
        if (!uniqueObject[objString]) {
            uniqueObject[objString] = true;
            uniqueArray.push(obj);
        }
    }

    return uniqueArray;
}

